        this.state={
            id:'',
        }

    }

deleteData = (e) => {

    

    let obj={
     id:e.target.id.value
    }
    

    axios.delete(`http://localhost:8111/${obj.id}`)
    .then((posRes)=>{

        console.log(posRes);

    },
    (errRes)=>{

        console.log(errRes);

    })
}

    render(){
        return(
            <div className="container " style={{width:100}}>
                <form onSubmit={this.deleteData}> 
                <h1>Delete student data</h1>
               <label> Student:<input type="number" name="id" style={{backgroundColor:"gray"}}/></label>
                <br/>
                <button>submit </button>
                </form>
            </div>
        )
    }
}
