import React from "react";
import axios from 'axios'
export default class InsertComponent extends React.Component {
constructor() {
super()
this.state = {
status: ''
}
}
render() {
return (
<div className="container mt-2">
<h1 className="text-primary">I am from Insert Component </h1>
<form onSubmit={this.insert} className="w-50 mx-auto">
<input type="number"
placeholder="Enter p_id"
name='p_id'
className="form-control my-2"></input>
<input type="text"
placeholder="Enter p_name"
name='p_name'
className="form-control my-2"></input>
<input type="number"
placeholder="Enter p_cost"
name='p_cost'
className="form-control my-2"></input>
<input type="submit" value='Create' className="btn
btn-outline-success"></input>
</form>
<h1 className="text-info">{this.state.status} </h1>
</div>
)
}
insert = (e) => {
e.preventDefault()
this.setState({
status: 'Loading'
})
let obj = {
p_id: parseInt(e.target.p_id.value),
p_name: e.target.p_name.value,
p_cost: parseInt(e.target.p_cost.value)
}
axios.post(`${url}/insert`, obj)
.then((posRes) => {
console.log(posRes)
this.setState({
status: posRes.data.insert
})
}, (errRes) => {
console.log(errRes)
})
}
}
