import React from "react";
import axios from 'axios'
export default class Student extends React.Component {
constructor() {
super()
this.state = {
status: ''
}
}
render() {
return (
<div className="container mt-2">
<h1 className="text-primary">Registration </h1>
<form onSubmit={this.insert} className="w-50 mx-auto">

<input type="text"
placeholder="Enter student name"
name='name'
className="form-control my-2"></input>
<input type="text"
placeholder="Enter city"
name='city'
className="form-control my-2"></input>
<input type="submit" value='Create' className="btn
btn-outline-success"></input>
</form>

</div>
)
}
insert = (e) => {
e.preventDefault()
this.setState({
status: 'Loading'
})
let obj = {
p_id: parseInt(e.target.p_id.value),
p_name: e.target.p_name.value,
p_cost: parseInt(e.target.p_cost.value)
}
axios.post(`http://localhost:8111/`, obj)
.then((posRes) => {
console.log(posRes)
this.setState({
status: posRes.data.insert
})
}, (errRes) => {
console.log(errRes)
})
}
}
