import React from "react";
import axios from 'axios'
export default class Student extends React.Component{

    constructor(props){
        super(props);

        this.state={

            name:" ",
            city:" ",
            response: null,
            erroe : null

        };

    }

    

   handleChange=(event)=>{
        this.setState({[event.target.data]:event.target.value});
    }

    register = async(event) =>{

        event.preventDefault();
        try{

            const respose = await axios.post('http://localhost:8111/',{
                name:this.state.name,
                city:this.state.city
            });
             this.setState({respose:respose.data, error:null});
        }
        catch(error){

            this.setState({error:error.respose ? error.respose.data:error.message});


        }
    }


    render(){
        return(

            <div className="container mt-2">
            <h1 className="text-primary">I am from Insert Component </h1>
            <form onSubmit={this.register} className="w-50 mx-auto">
            <input type="text"
            placeholder="Name"
            name='name'
            className="form-control my-2"></input>
            <input type="text"
            placeholder="City"
            name='city'
            className="form-control my-2"></input>
            
            <input type="submit" value='Create' className="btn
            btn-outline-success"></input>
            </form>
            <h1 className="text-info">{this.state.status} </h1>
            </div>
        )
    }
}
