import React from "react";
import axios from 'axios'
export default class Student extends React.Component{

    constructor(props){
        super(props);

        this.state={

            name:" ",
            city:" ",
            response: null,
            erroe : null

        };

    }

    

   handleChange=(event)=>{
        this.setState({[event.target.data]:event.target.value});
    }

    register = async(event) =>{

        event.preventDefault();
        try{

            const respose = await axios.post('http://localhost:8111/',{
                name:this.state.name,
                city:this.state.city
            });
             this.setState({respose:respose.data, error:null});
        }
        catch(error){

            this.setState({error:error.respose ? error.respose.data:error.message});


        }
    }


    render(){
        return(

            <div className="table table-bordered w-200 ">
                <form onSubmit={this.register}>
                    
                
                    <h1 style={{color:"blue"}}>Registration</h1>
                
                   <label> Name:
                    <input type="text" onChange={this.handleChange} />
                    </label>
                    <br/>

                    <label> City:
                    <input type="text"  onChange={this.handleChange}/>
                    </label>
                    
                    <br/>
                
                
                
                    <button type="submit">Submit</button>
                
                </form>
            </div>
        )
    }
}
