import React from "react";

export default class StateChange extends React.Component{

    constructor(){
        super()
        this.state={
            msg:'Hello_World',
            count:0
        }
    }
    render(){
        return(
            <div>
                <p style={{color:'palegoldenrod'}}>{this.state.msg}</p>
                <button onClick={this.onChange} className="btn btn-primary w-10 btn-hover ">Change</button>
           <button onClick={this.add} className="btn btn-secondary btn-hover w-10">+</button>
           <button style={{color:'red'}}>{this.state.count}</button>

           <button onClick={this.sub} className="btn btn-secondary btn-hover w-10">-</button>
           
            </div>
        )
    }

    onChange=()=>{
        this.setState({msg:this.state.msg})
        this.setState(prevState=>{
            prevState.msg='Hello Equinox'
        })

        add=()=>{
            this.setState({count:count+1})
            this.setState(prevState=>{
               prevState.count=count
            })
        }

        sub=()=>{
            this.setState({count:count-1})
            this.setState(prevState=>{
                prevState.count=count
            })
        }
    }
}
